clear;
tic;

Central.N_UnitCell = 1;
Central.N_row = 1; %[control:editfield:2d90]{"position":[17,18]}
Central.N_col = 20000; %[control:editfield:211e]{"position":[17,22]}
N_Disorder = 11; %[control:editfield:9e82]{"position":[14,16]}
BoundaryCond = "OBC"; %[control:dropdown:13ce]{"position":[16,21]}
Central.BoundaryCond = BoundaryCond;
% Central.J = 1000;
epsilon = 0 * ones(1,Central.N_UnitCell);
Central.mu = 0;
% 中心区其他量
% 总格点数
N_tot_site = Central.N_row * Central.N_col * Central.N_UnitCell;
N_Cells = Central.N_row * Central.N_col;
% 每列格点数
N_site_pcol = Central.N_row * Central.N_UnitCell;
% 跃迁
t = -1;
% 格点标号
eta = 0;
FermiEnergy = 0;

W = logspace(-10,-1,N_Disorder);
LocLength = zeros(1, length(W));
% Loop for disorder and delta simutaneously
for kk = 1:length(W)
    [H_CC, V_CC] = Hamiltonian_Metal(epsilon, t, Central.N_row);
    r = rank(V_CC);
    R = eye(size(H_CC,1));
    R_Spectrum = zeros(2*r, Central.N_col);
    for ii = 1:Central.N_col
        if ii > 1
            T = Transfer_Matrix(H_CC, V_CC, FermiEnergy, W(kk))*Q;
        else
            T = Transfer_Matrix(H_CC, V_CC, FermiEnergy, W(kk));
        end
        [Q, R] = qr(T);
        [R_diag, ind] = sort(diag(R), 'descend','ComparisonMethod','abs');
        Q = Q(:, ind);
        R_Spectrum(:, ii) = abs(R_diag);
    end
    lnR = log(R_Spectrum);        
    Lyapunov_Spectrum = sum(lnR,2)/Central.N_col;        
% The minimal Lyapunov exponent corresponds to the largest localization
% length
    LocLength(kk) = 1./min(abs(Lyapunov_Spectrum));
    % disp(['Lyapunov Exponent:',num2str(min(abs(Lyapunov_Spectrum)))])
    % disp(['Localization:',num2str(1./min(abs(Lyapunov_Spectrum)))])
    % figure
    % plot(1:Central.N_col, cumsum(lnR,2)./(1:Central.N_col))
    % figure
    % plot(1:Central.N_col, (1:Central.N_col)./cumsum(lnR,2))
end
%%
plot(W, LocLength); %[output:442966ab]
yscale log %[output:442966ab]
xscale log %[output:442966ab]
hold off %[output:442966ab]


toc; %[output:5fc8ec0d]
function T = Transfer_Matrix(H_CC, V_CC, FermiEnergy, Disorder)
eta = 0;
[V, Xi, W] = svd(V_CC);
r = rank(Xi);
V = V(:, 1:r);
W = W(:, 1:r);
Xi = Xi(1:r, 1:r);
H_imp = Disorder * diag(rand(1,size(H_CC,1))-0.5);
G = inv((FermiEnergy + 1i * eta) * eye(size(H_CC,1)) - H_CC - H_imp);
G_vv = (V' * G * V) * Xi;
G_ww = (W' * G * W) * Xi;
G_vw = (W' * G * V) * Xi;
G_wv = (V' * G * W) * Xi;

theta = angle(det(G_vw));


T = [inv(G_vw), -inv(G_vw) * G_ww; 
    G_vv * inv(G_vw), G_wv - G_vv * inv(G_vw) * G_ww];
T = exp(1i * theta / r) * T;
end


function [H00, H01] = Hamiltonian_Metal(epsilon, t, Width)
    H00 = epsilon * eye(Width) + t * (diag(ones(1, Width-1), +1) + diag(ones(1, Width-1), -1));
    H01 = t * eye(Width);
end


