%[text] # 三对角分块矩阵的特定块求逆
%[text] Supplementary of [RKKY interaction in one-dimensional flat-band lattices | Phys. Rev. B](https://journals.aps.org/prb/abstract/10.1103/PhysRevB.108.155429)
%[text] ![](text:image:1d6c)
%[text] ## 形如这样的矩阵，其对应block的逆元可以通过快速的方法计算，计算效率随系统尺寸增加线性增加
%[text] 输入:
%[text] ### a: 矩阵列表，实际为 $\[a\_i\]\_{jk}$,其中jk为第一，第二个维度，i为第三个维度
%[text] ### b: 矩阵列表，实际为 $\[b\_i\]\_{jk}$,其中jk为第一，第二个维度，i为第三个维度
%[text] ### m,n: 正整数，对应为希望求解的逆$A^{-1}$的第m行第n列分块矩阵
function inv_target_block = tridiag_block_inv(a, b, m, n, varargin)

[blocksize, ~, N_block] = size(a);
inv_target_block = complex(zeros(blocksize));
% 当N_block为1时，代表a_i=a，此时varargin{1}设置为重复的Block数
% 
% 
if nargin < 5
    % 递推计算D, U, L
    D = complex(zeros(blocksize, blocksize, N_block));
    U = complex(zeros(blocksize, blocksize, N_block-1));
    L = complex(zeros(blocksize, blocksize, N_block-1));
    
    % 递推计算D, U, L
    D(:,:,N_block) = a(:,:,N_block);
    for ii = N_block-1:-1:1
        U(:, :, ii) = b(:, :, ii) / D(:, :, ii+1);
        L(:, :, ii) = (D(:, :, ii+1) \ b(:, :, ii)');
        D(:, :, ii) = a(:, :, ii) - U(:, :, ii) * b(:, :, ii)';
    end
    
    % 计算D逆
    Dinv = pageinv(D);
    % 计算Uinv(m,k)和Linv(k,n)
    Ainv = Dinv(:, :, 1);
    
    if m == n
        for ii = 2: m
            Ainv = Dinv(:, :, ii) + L(:, :, ii-1) * Ainv * U(:, :, ii-1);
        end
        inv_target_block = Ainv;
    elseif m > n
        for ii = 2: n
            Ainv = Dinv(:, :, ii) + L(:, :, ii-1) * Ainv* U(:, :, ii-1);
        end
        for ii = (n+1) :m
            Ainv = -L(:, :, ii-1) * Ainv;
        end
        inv_target_block = Ainv(:, :, m, n);
    elseif m < n
        for ii = 2: m
            Ainv = Dinv(:, :, ii) + L(:, :, ii-1) * Ainv * U(:, :, ii-1);
        end
        for ii = (m+1) :n
            Ainv = -Ainv * U(:, :, ii-1);
        end
        inv_target_block = Ainv;
    end
else
    N_block = varargin{1};

    % 递推计算D, U, L
    D = zeros(blocksize, blocksize, N_block);
    U = zeros(blocksize, blocksize, N_block-1);
    L = zeros(blocksize, blocksize, N_block-1);
    
    % 递推计算D, U, L
    D(:,:,N_block) = a;
    for ii = N_block-1:-1:1
        U(:, :, ii) = b / D(:, :, ii+1);
        L(:, :, ii) = (D(:, :, ii+1) \ b');
        D(:, :, ii) = a - U(:, :, ii) * b';
    end
    
    % 计算D逆
    Dinv = pageinv(D);
    % 计算Uinv(m,k)和Linv(k,n)
    Ainv = Dinv(:, :, 1);
    
    if m == n
        for ii = 2: m
            Ainv = Dinv(:, :, ii) + L(:, :, ii-1) * Ainv * U(:, :, ii-1);
        end
        inv_target_block = Ainv;
    elseif m > n
        for ii = 2: n
            Ainv = Dinv(:, :, ii) + L(:, :, ii-1) * Ainv* U(:, :, ii-1);
        end
        for ii = (n+1) :m
            Ainv = -L(:, :, ii-1) * Ainv;
        end
        inv_target_block = Ainv(:, :, m, n);
    elseif m < n
        for ii = 2: m
            Ainv = Dinv(:, :, ii) + L(:, :, ii-1) * Ainv * U(:, :, ii-1);
        end
        for ii = (m+1) :n
            Ainv = -Ainv * U(:, :, ii-1);
        end
        inv_target_block = Ainv;
    end
end
end

